INSERT INTO `luxuryshop`.`luxury`(`LID`, `AID`, `LNAME`, `PRICE`, `RESTNUM`, `TYPE`, `DATE`, `INFOR`, `IMAGE`, `SALENUM`, `VIEWCOUNT`, `SIZE`, `WEIGHT`, `COLOR`) VALUES (36, 1, '针织棉短裤',                       7500, 20, '服饰', '2020-06-16', '这款短裤两侧饰有互扣式双G条纹，这一别致装饰贯穿于Gucci最新系列。侧边饰带为Gucci经典标识融入运动活力，唤起人们对于复古运动装常见品牌标识饰带的回忆。', 'client/product/image/fushi/cloth-16.jpg', 0, 133, 'XM-XL-XML', 9999, '空军绿');
INSERT INTO `luxuryshop`.`luxury`(`LID`, `AID`, `LNAME`, `PRICE`, `RESTNUM`, `TYPE`, `DATE`, `INFOR`, `IMAGE`, `SALENUM`, `VIEWCOUNT`, `SIZE`, `WEIGHT`, `COLOR`) VALUES (37, 1, '饰Gucci标签羊毛夹克',              17600, 20, '服饰', '2020-06-16', '缝制夹克运用革新的美学理念进行重新演绎，在经典设计中注入现代精髓，成为融合精致面料和热门休闲材质的混搭造型的主打单品。这款夹克采用传统设计，由蓝色棉布制作而成，可与配套的背心和牛仔裤演绎出众造型，颠覆传统剪裁风格。', 'client/product/image/fushi/cloth-17.jpg', 0, 133, 'XM-XL-XML', 9999, '黑');
INSERT INTO `luxuryshop`.`luxury`(`LID`, `AID`, `LNAME`, `PRICE`, `RESTNUM`, `TYPE`, `DATE`, `INFOR`, `IMAGE`, `SALENUM`, `VIEWCOUNT`, `SIZE`, `WEIGHT`, `COLOR`) VALUES (38, 1, '饰Gucci标签补丁效果牛仔夹克',       18500, 20, '服饰', '2020-06-16', '2020春夏系列深入探索了牛仔布这一经典材质，运用多种不同造型和处理赋予该面料新的活力。这款牛仔夹克使用多种工艺进行特殊水洗处理后，呈现出做旧补丁效果，尽显复古风情。胸口处巧妙融入法语格言“Gucci quelle qu’en soit la saison”刺绣标签。该格言的意思是“Gucci服饰四季皆宜”，充分诠释了Gucci服饰可自由穿搭的设计理念。', 'client/product/image/fushi/cloth-18.jpg', 0, 133, 'XM-XL-XML', 9999, '牛仔蓝');
INSERT INTO `luxuryshop`.`luxury`(`LID`, `AID`, `LNAME`, `PRICE`, `RESTNUM`, `TYPE`, `DATE`, `INFOR`, `IMAGE`, `SALENUM`, `VIEWCOUNT`, `SIZE`, `WEIGHT`, `COLOR`) VALUES (39, 1, '刺绣绳绒卫衣',                     8300, 20, '服饰', '2020-06-16', '这款黑色绳绒卫衣采用已经成为品牌标志的浪漫箴言，正面饰有 Gucci、“Maison de LAmour”和“Sine Amore Nihil”刺绣，短袖设计，装饰同色醋酸纤维饰边。灵感源自运动装和休闲装的风格元素相互交织，以精致柔软的面料呈现动感造型，演绎 Gucci 经典风格', 'client/product/image/fushi/cloth-19.jpg', 0, 133, 'XM-XL-XML', 9999, '黑');
